import requests # Se importa la libreria requests para manejar las peticionesfrom app.config import FACEIT_API_KEY, FACEIT_API_URL # Archivo config para manejar la API Key como variable de entorno#Funcion para hacer la request a las APIs de FACEITdef hacer_request(endpoint, parametros=None):    url = f"{FACEIT_API_URL}/{endpoint}"  # Se le añade el endpoint a la URL base    headers = {"Authorization": f"Bearer {FACEIT_API_KEY}"}  # Cabeza de la funcion    # Si parametros es None, se asigna un diccionario vacío (Hay veces que no es necesario asignar parametros)    if parametros is None:        parametros = {}    response = requests.get(url, headers=headers, params=parametros)  # Cuerpo que devuelve la peticion a la API    if response.status_code == 200:  # Si se recibe un codigo 200 (OK), se devuelve el cuerpo en JSON        return response.json()    # Se devuelve un diccionario de error en caso de que no sea un 200. En algunas funciones es necesario antes del procesamiento de datos    # Antes, si la API devolvía un error, esta función devuelve un entero, entonces al luego hacer el .get() sobre ese número, causa error    else:        return {            "error": True,            "status_code": response.status_code,            "mensaje": response.json()        }